module.exports = meta

function meta (text, cb) {
  if (!text) text = ''
  var info = {}
  info.tech = tech.filter(x=>~text.indexOf(x))
  info.antitech = antitech.filter(x=>~text.indexOf(x))
  info.text = text.split('\n').filter(x=>x.trim()!=='')
  cb(null,info)
}

var tech = [
  ' js ',
  ' npm ',
  ' browserify ',
  // ' remote ',
  ' javascript ',
  ' cordova ',
  ' phonegap '
  // ' css ',
  // ' html ',
  // ' svg '
]
var antitech = [
  ' php ',
  ' django ',
  ' python ',
  ' ruby ',
  ' rails ',
  ' java ',
  ' rust ',
  ' clojure ',
  ' elm ',
  ' coffeescript ',
  ' wordpress ',
  ' drupal ',
  ' yoomla ',
  ' ionic ',
  ' angular '
]
