// https://www.npmjs.com/package/firebase-event-stream
// https://www.npmjs.com/package/firebase-read-stream
var request = require('superagent') // @TODO: duplex stream? send/receive?

module.exports = send

function send (name, data, cb) {
  if (typeof cb !== 'function') cb = function () {}
  // @TODO: evaluate data, whether it should be stored or not!
  // e.g. do not send ANTITECH STUFF
  var url = `https://scraping-a5a55.firebaseio.com/${name}.json`
  request
    .post(url)
    .send(data)
    .set('Accept', 'application/json')
    .end(cb)
}
