
module.exports = function (data, cb) {

  if (!data || typeof data !== 'object') {
    return cb(new Error('no `data` given'))
  }
  if (hasInvalid_skills(data.skills)) {
    data.skills = '<empty>' // new Error('`data.skills` is invalid')
  }
  if (hasInvalid_requirements(data.requirements)) {
    data.requirements = '<empty>' // new Error('`data.requirements` is invalid')
  }
  if (hasInvalid_title(data.title)) {
    data.title = '<empty>' // new Error('`data.title` is invalid')
  }
  if (hasInvalid_type(data.type)) {
    data.type = '<empty>' // new Error('`data.type` is invalid')
  }
  if (hasInvalid_payment(data.payment)) {
    data.payment = '<empty>' // new Error('`data.payment` is invalid')
  }
  if (hasInvalid_duration(data.duration)) {
    data.duration = '<empty>' // new Error('`data.duration` is invalid')
  }
  if (hasInvalid_budget(data.budget)) {
    data.budget = '<empty>' // new Error('`data.budget` is invalid')
  }
  if (hasInvalid_description(data.description)) {
    data.description = '<empty>' // new Error('`data.description` is invalid')
  }
  if (hasInvalid_details(data.details)) {
    data.details = '<empty>' // new Error('`data.details` is invalid')
  }
  if (hasInvalid_company(data.company)) {
    data.company = '<empty>' // new Error('`data.company` is invalid')
  }
  if (hasInvalid_location(data.location)) {
    data.location = '<empty>' // new Error('`data.location` is invalid')
  }
  if (hasInvalid_benefits(data.benefits)) {
    data.benefits = '<empty>' // new Error('`data.benefits` is invalid')

  }
  return cb(null, data)

  function hasInvalid_skills (data) {
    if (data && data.length) return false
    return true
  }
  function hasInvalid_requirements (data) {
    if (data && data.length) return false
    return true
  }
  function hasInvalid_title (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_type (data) {
    if (data === 'job' || data === 'freelance') return false
    return true
  }
  function hasInvalid_payment (data) {
    if (data === 'fixed' || data === 'per hour') return false
    return true
  }
  function hasInvalid_duration (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_budget (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_description (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_details (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_company (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_location (data) {
    if (typeof data === 'string') return false
    return true
  }
  function hasInvalid_benefits (data) {
    if (typeof data === 'string') return false
    return true
  }
}
